                        this.$('.chatroom-body').empty()
                            .append(tpl_chatarea({
                                    'unread_msgs': __('You have unread messages'),
                                    'show_toolbar': _converse.show_toolbar,
                                    'label_message': __('Message'),
                                    'show_send_button': _converse.show_send_button
                                }))
                            .append(this.occupantsview.$el);
